import React, { Component } from 'react'
class Terms extends Component {
  constructor (props) {
    super(props)
    this.state = {
      signed_agreement: false
    }
  }

  handleVideoEnd () {
    console.log('Finished')
  }

  handleChange () {
    this.setState({
      signed_agreement: !this.state.signed_agreement
    })
  }

  render () {
    if (this.props.currentStep !== 4) {
      return null
    } else {
      return (
        <div class='page-wraper account-body accountbg'>
          <h3>Terms and conditions</h3>
          <div className='card-body'>
            <div class='profileVideo'>
              <p>We will approve link requests from these organizations if we decide that: (a) the link would not make us look unfavorably to ourselves or to our accredited businesses; (b) the organization does not have any negative records with us; (c) the benefit to us from the visibility of the hyperlink compensates the absence of Company Name; and (d) the link is in the context of general resource information.</p>
            </div>
          </div>
          <div className='card-footer'>
            <div class='form-check checkflex'>
              <input type='checkbox' class='form-check-input' name='signed_agreement' id='signed_agreement' />
              <label class='form-check-label' for='signed_agreement'>
                Agree for Terms & conditions
              </label>
            </div>
            <div class='form-check checkflex'>
              <input type='checkbox' class='form-check-input' name='payment_terms' id='payment_terms' />
              <label class='form-check-label' for='payment_terms'>
                Agree for Auto Paymennt
              </label>
            </div>
          </div>
        </div>
      )
    }
  }
}

