import React, { Component } from 'react'
import { FileUpload, GetUserProfile, UpdateUserProfile } from './services'
import Swal from 'sweetalert2'
import '../../src/App.css'
class Profile extends Component {
  constructor (props) {
    super(props)
    this.state = {
      firstname: null,
      lastname: null,
      email: null,
      phone: null,
      photo: "https://www.seekpng.com/png/detail/966-9665317_placeholder-image-person-jpg.png",
      emailReadOnly: false,
      audio: null,
      fileUploadCheck: false,
      is_profile_updated: 0
    }
  }

  componentDidMount() {
    this.profile()
    this.getUserProfile()
  }

  // handleChange(event) {
  //   const { name, value } = event.target;
  //   this.setState({
  //     [name]: value
  //   });
  // }

  profile = () => {
    if (this.props.currentStep !== 1) {
      return null
    }
  }

  onFileChange = async (type, e) => {
    this.setState({ fileUploadCheck: true })
    if (type == 'audio') {
      this.setState({ audioBtnTxt: 'Uploading....' })
    }
    var converb64 = await this.toBase64(e.target.files[0])
    var b64 = (converb64).split(",")
    var data = {
      file:  b64[1],
      type: e.target.files[0].type
    }
    var profilePhoto = await FileUpload(data)
    if(profilePhoto.data.error) {
      Swal.fire({
        icon: 'error',
        title: 'OOPS',
        text: profilePhoto.data.msg,
        showConfirmButton: false,
        timer: 1500
      })
    } else {
      this.setState({ [type]: profilePhoto.data.data.url,fileUploadCheck: false })
      if (type == 'audio') {
        this.setState({ audioBtnTxt: 'Audio uploaded Successfully' })
      }
    }
  }

  toBase64 = file => new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
  });

  getUserProfile = async () => {
    var profile = await GetUserProfile()
    if(profile.data.error) {
       Swal.fire({
          icon: 'error',
          title: 'OOPS',
          text: profile.data.msg,
          showConfirmButton: false,
          timer: 1500
      })
      window.location.href = '/membership-profile';
    } else {
       const { firstname, lastname, cell, email, photo, type, audio, is_profile_updated, watched_safety_video, signed_agreement } = profile.data.data
       if (is_profile_updated== 1 && watched_safety_video == 1 && signed_agreement == 1) {
        window.location.href = '/membership-completed';
       } else {
        this.setState({
          firstname: firstname,
          lastname: lastname,
          phone: cell,
          email: email,
          photo: photo == "" || photo == null ? this.state.photo : photo,
          audio: audio == "" || audio == null ? this.state.audio : null
        })
        this.props.handleChange({ target: { name: 'firstname', value: firstname }})
        this.props.handleChange({ target: { name: 'lastname', value: lastname }})
        this.props.handleChange({ target: { name: 'phone', value: cell }})
        this.props.handleChange({ target: { name: 'email', value: email }})
        if (type != 'manual') {
          this.setState({ emailReadOnly: true })
        }
       }
    }
 }

 updateUserProfile = async () => {
  const { firstname, lastname, phone, email, photo, audio } = this.state
  var data = {
    firstname: firstname,
    lastname: lastname,
    phone: phone,
    email: email,
    photo: photo,
    audio: audio
  }
  var userProfile = await UpdateUserProfile(data)
  if(userProfile.data.error) {
    Swal.fire({
      icon: 'error',
      title: 'OOPS',
      text: userProfile.data.msg,
      showConfirmButton: false,
      timer: 1500
    })
  } else {
    Swal.fire({
      icon: 'success',
      title: 'Profile Update',
      text: 'Your profile has been updated successfully',
      showConfirmButton: false,
      timer: 1500
    })
  }
 }
 onChange = (key, value) => {
  this.setState({
      [key]: value
  });
}
  render () {
    if (this.props.currentStep !== 1) {
      return null
    } else {
      return (
        <div className='page-wraper account-body accountbg'>
          <div>
            <h3 className='heading'>Profile</h3>
            <div className='text-center'>
              <div className='profile'>
                <img src={this.state.photo} height='50' alt='logo' className='auth-logo' />
                <div className='image-upload'>
                  <label for='file-input'>
                    <i className='fa fa-camera' aria-hidden='true' />
                  </label>
                  <input id='file-input' type='file' accept='image/png,image/jpeg,image/jpg' onChange={(e) => this.onFileChange('photo', e)} />
                </div>
              </div>
            </div>
            <div className='card-body'>
              <form className='form-horizontal auth-form' action='index.html'>
                <div className='form-group'>
                  <label for='firstname'>Firstname</label>
                  <div className='input-group mb-3'>
                    <input type='text' className='form-control' value={this.state.firstname} name='firstname' id='firstname' onChange={this.props.handleChange} placeholder='Enter firstname' />
                  </div>
                </div>
                <div className='form-group'>
                  <label for='lastname'>Lastname</label>
                  <div className='input-group mb-3'>
                    <input type='text' className='form-control' value={this.state.lastname} name='lastname' id='lastname' onChange={this.props.handleChange} placeholder='Enter lastname' />
                  </div>
                </div>
                <div className='form-group'>
                  <label for='email'>Email id</label>
                  <div className='input-group mb-3'>
                    <input type='text' className='form-control' value={this.state.email} name='email' id='email' onChange={this.props.handleChange} placeholder='Enter email' disabled={this.state.emailReadOnly} />
                  </div>
                </div>
                <div className='form-group'>
                  <label for='phone'>Phone no</label>
                  <div className='input-group mb-3'>
                    <input type='text' className='form-control' value={this.state.phone} name='phone' id='phone' onChange={this.props.handleChange} placeholder='Enter phone number' />
                  </div>
                </div>
                {/* <div className='form-group'>
                  <div className='upload-btn-wrapper'>
                    <button className='btn'>{this.state.audioBtnTxt}</button>
                    <input type='file' accept='audio/mp3,audio/aac,audio/wav' onChange={(e) => this.onFileChange('audio', e)} />
                  </div>
                </div> */}
                {/* <div className='form-group mb-0 row'>
                  <div className='col-md-12'>
                    <button className='btn btn-primary loginbtn btn-block waves-effect waves-light' type='button' onClick={(e) => this.updateUserProfile()} disabled={this.state.fileUploadCheck}>Submit <i className='fa fa-sign-in' aria-hidden='true' /> </button>
                  </div>
                </div> */}
              </form>
            </div>
          </div>
        </div>
      )
    }
  }
}

export default Profile;
